import { Component } from '@angular/core';
import { SidebarService } from 'src/app/services/sidebar.service';
import { SideBarProperties } from 'src/app/SideBarProperties';
import { ToolTipProperties } from 'src/app/ToolTipProperties';
import { SidebarPropertiesItemComponent } from './sidebar-properties-item/sidebar-properties-item.component';

@Component({
	selector: 'app-sidebar-properties',
	templateUrl: './sidebar-properties.component.html',
	styleUrls: ['./sidebar-properties.component.css'],
})
export class SidebarPropertiesComponent {
	sideBarProperties: SideBarProperties[] = [];
	toolTipProperties: ToolTipProperties[] = [];
	constructor(
		private sideBarService: SidebarService,
		private toolTipService: SidebarService
	) {}
	ngOnInit() {
		this.sideBarService
			.getSideBarProperties()
			.subscribe(
				(properties: SideBarProperties[]) =>
					(this.sideBarProperties = properties as SideBarProperties[])
			);
		this.toolTipService
			.getToolTipProperties()
			.subscribe((properties: ToolTipProperties[]) => {
				this.toolTipProperties = properties as ToolTipProperties[];
			});
	}

	onRegionClick(property: any) {
		console.log(property);
	}

	showToolTip(property: any) {
		let toolTip = document.querySelectorAll('.regions')[property.id - 1];
		const index = Number([property.id - 1]);
		console.log(index);
		console.log(this.toolTipProperties[0]);
		let html = `
		<div class="tooltip-container">
			<div class="tooltip-title">
				<div class="tooltip-title-header"> ${this.toolTipProperties[index].name} </div>
				<div class="period-property-updated"> ${this.toolTipProperties[index].days} </div>
			</div>
			<hr>
			<div>
				<div class="first-row-tooltip-container">
					<div class="tooltip-image"> <img src="../../../../assets/images/property-images/property${this.toolTipProperties[index].id}.jpg" alt="sedona apartments"
							class="tooltip-img-s"> </div>
					<div class="tooltip-description">
						<div> Location: ${this.toolTipProperties[index].location} </div>
						<div> Phone:${this.toolTipProperties[index].phone} </div>
						<div> Email: ${this.toolTipProperties[index].email} </div>
						<div> Units: ${this.toolTipProperties[index].units} </div>
					</div>
				</div>
				<div class="second-row-tooltip-container">
					<div>
						<h4> NER </h4> <b>$794</b>
					</div>
					<div>
						<h4> NER/sqft </h4> <b>$794</b>
					</div>
					<div>
						<h4> Concess. </h4> <b>$794</b>
					</div>
					<div>
						<h4> Occ. </h4> <b>$794</b>
					</div>
					<div>
						<h4> Traffic/Wk </h4> <b>$794</b>
					</div>
					<div>
						<h4> Leases/Wk </h4> <b>$794</b>
					</div>
				</div>
			</div>
		</div>

		`;
		toolTip.innerHTML = '';
		toolTip.insertAdjacentHTML('beforeend', html);
	}
}
