import { Component } from '@angular/core';
import { SidebarService } from 'src/app/services/sidebar.service';
import { SideBarProperties } from 'src/app/SideBarProperties';
import { SidebarPropertiesItemComponent } from './sidebar-properties-item/sidebar-properties-item.component';

@Component({
	selector: 'app-sidebar-properties',
	templateUrl: './sidebar-properties.component.html',
	styleUrls: ['./sidebar-properties.component.css'],
})
export class SidebarPropertiesComponent {
	sideBarProperties: SideBarProperties[] = [];

	constructor(private sideBarService: SidebarService) {}
	ngOnInit() {
		this.sideBarService
			.getSideBarProperties()
			.subscribe(
				(properties: SideBarProperties[]) =>
					(this.sideBarProperties = properties as SideBarProperties[])
			);
	}

	onRegionClick(property: any) {
		console.log(property);
	}

	showToolTip(property: any) {
		let htmlContent = `<div class="tooltip-container">
		<div class="tooltip-title">
			<div class="tooltip-title-header"> ${toolTipProperties[index].name} </div>
			<div class="period-property-updated"> ${toolTipProperties[index].days} </div>
		</div>
		<hr>
		<div>
			<div class="first-row-tooltip-container">
				<div class="tooltip-image"> <img src="./images/property${toolTipProperties[index].id}.jpg" alt="sedona apartments"
						class="tooltip-img-s"> </div>
				<div class="tooltip-description">
					<div> Location: ${toolTipProperties[index].location} </div>
					<div> Phone:${toolTipProperties[index].phone} </div>
					<div> Email: ${toolTipProperties[index].email} </div>
					<div> Units: ${toolTipProperties[index].units} </div>
				</div>
			</div>
			<div class="second-row-tooltip-container">
				<div>
					<h4> NER </h4> <b>$794</b>
				</div>
				<div>
					<h4> NER/sqft </h4> <b>$794</b>
				</div>
				<div>
					<h4> Concess. </h4> <b>$794</b>
				</div>
				<div>
					<h4> Occ. </h4> <b>$794</b>
				</div>
				<div>
					<h4> Traffic/Wk </h4> <b>$794</b>
				</div>
				<div>
					<h4> Leases/Wk </h4> <b>$794</b>
				</div>
			</div>
		</div>
	</div>`;

	}
}
